package com.run.treadmill.activity.home;import android.content.Context;import com.run.treadmill.activity.home.help.HomeMcu;import com.run.treadmill.activity.runMode.RunningParam;import com.run.treadmill.base.BasePresenter;import com.run.treadmill.sp.SpManager;import java.util.Arrays;public class HomePresenter extends BasePresenter<HomeView> {    private Context mContext;    void setContext(Context context) {        this.mContext = context;    }    public void setUpRunningParam() {        RunningParam.reset();        Arrays.fill(RunningParam.getInstance().mInclineArray, 0.0f);        Arrays.fill(RunningParam.getInstance().mSpeedArray, SpManager.getMinSpeed(SpManager.getIsMetric()));    }    /**     * 检测加油提示和加锁     */    void checkLubeAndLock() {        if (SpManager.getMaxLubeDis() != 0 && SpManager.getMaxLubeDis() - SpManager.getRunLubeDis() <= 0) {            getView().showLube();            return;        }        if ((SpManager.getBackUpTotalRunDis() > 0 && (SpManager.getBackUpTotalRunDis() - SpManager.getBackUpRunDis() <= 0))                || (SpManager.getBackUpRunTotalTime() > 0 && (SpManager.getBackUpRunTotalTime() - SpManager.getBackUpRunTime() <= 0))) {            getView().showLock();            return;        }    }    @Override    public void onSucceed(byte[] data, int len) {        super.onSucceed(data, len);        HomeMcu.onSucceed(data, len);    }    void reSetSleepTime() {        getView().reSetSleepTime();    }    void wakeUpSleep() {        getView().wakeUpSleep();    }}